com.webreach.mirth.model.Step.script:
var i=0;

//var HL7DateFmt = "yyyyMMddHHmmss.SSSSZ";
var HL7DateFmt = "yyyyMMddHHmmss";
var XMLDateFmt = "yyyy'-'MM'-'dd'T'hh:mm:ss";

//Convert the Birth Date
var recordtarget_patientrole_patient_birthtime_attValue = channelMap.get('recordtarget_patientrole_patient_birthtime_attValue');
if (recordtarget_patientrole_patient_birthtime_attValue != 0)
{
	recordtarget_patientrole_patient_birthtime_attValue = DateUtil.convertDate(HL7DateFmt,XMLDateFmt,recordtarget_patientrole_patient_birthtime_attValue);
	channelMap.put('recordtarget_patientrole_patient_birthtime_attValue',recordtarget_patientrole_patient_birthtime_attValue);
}

var patRole = msg['recordTarget']['patientRole'];

var Ids = channelMap.get('Ids');

for each (indId in patRole..id)
{


       var thisId=new Object();
	   Ids[i] = thisId;

       thisId.value=indId['@extension'].toString();
	   thisId.type=indId['@root'].toString();

       i++;
}

//Reset Counter
i = 0;

var Telcom = channelMap.get('Telcom');
var telco;
for each (tel in patRole..telecom)
{	
	telco = new Object();
    Telcom[i] = telco;
	telco.type=tel['@use'].toString();
	telco.value=tel['@value'].toString();
	i++;
}

//Reset Counter
i = 0;
//Find the Legal Name and Parse out the other names
var Names = channelMap.get('Names');
var nameObj;
var first;
var middle;

for each (name in patRole..name)
{
	first = "";
	middle = "";

	if (name['given']['1'] != null )
	{
		first = name['given']['0'].toString();
		middle = name['given']['1'].toString();
	}
	else
	{
		first = name['given'].toString();
	}
	//Name Types: L - Legal, BR = Birth, AD = Adoption, P = Past/ Alias
	if (name['@use'].toString()=="L")
	{
		channelMap.put("legalGivenName",first);
		channelMap.put("legalMiddleName",middle);
		channelMap.put("legalFamilyName",name['family'].toString());
	} 
	else
	{
		nameObj =new Object();

		Names[i] = nameObj;

		nameObj.type=name['@use'].toString();
		nameObj.givenName=first;
		nameObj.middleName = middle;
		nameObj.familyName=name['family'].toString();
	   	i++;
	}	
}

//Reset Counter
i = 0;

var addresses = channelMap.get('Addresses');
for each (iaddr in patRole..addr)
{


       var address=new Object();
	   addresses[i] = address;
       address.use=iaddr['@use'].toString();
	   address.street = iaddr['streetAddressLine'].toString();
       address.city = iaddr['city'].toString();
       address.state = iaddr['state'].toString();
       address.postalCode = iaddr['postalCode'].toString();
       address.country = iaddr['country'].toString();
       address.typeName="";
       address.typeCodeSystem="";
       address.typeCodeSystemName="";


       i++;
}

