com.webreach.mirth.model.Step.script:
var HL7DateFmt = "yyyyMMddHHmmss";
//var HL7DateFmt = "yyyyMMddHHmmss.SSSSZ";
var XMLDateFmt = "yyyy'-'MM'-'dd'T'hh:mm:ss";

var orderdatetime = channelMap.get('orderdatetime');
if (orderdatetime.length() != 0)
{
	orderdatetime = DateUtil.convertDate(HL7DateFmt,XMLDateFmt,orderdatetime);
 	channelMap.put('orderdatetime',orderdatetime);
}

var ordereffectivedatetime = channelMap.get('ordereffectivedatetime');
if (ordereffectivedatetime.length() != 0)
{
	ordereffectivedatetime = DateUtil.convertDate(HL7DateFmt,XMLDateFmt,ordereffectivedatetime);
 	channelMap.put('ordereffectivedatetime',ordereffectivedatetime);
}

